class Solution:
    def maxOperations(self, s: str) -> int:
        s = s.rstrip("1")
        ans = 0
        running = 0
        for i in range(len(s) - 1):
            nextIsOne = s[i + 1] == "1"
            if s[i] == "1" and not nextIsOne:
                running += 1
                ans += running 
            elif s[i] == "1" and nextIsOne:
                running += 1
        return ans
