class Solution:
    def maxOperations(self, s: str) -> int:
        one_groups = []
        cur_group = 0

        for c in s:
            if c == "1":
                cur_group += 1
            else:
                if cur_group > 0:
                    one_groups.append(cur_group)
                    cur_group = 0

        # print(one_groups)
        ans = 0
        running_sum = 0
        for num in one_groups:
            running_sum += num
            ans += running_sum

        return ans
