class Solution:
    def maxOperations(self, s: str) -> int:
        ans, count = 0, 0
        s = list(s)
        while s:
            if s[-1] == '1':
                ans += count
                s.pop()
            else:
                while s and s[-1] == '0':
                    s.pop()
                count += 1
        return ans
