class Solution {
public:
    int maxOperations(string s) {
        int ans=0,c=0;
        int n=s.size();
        for(int i=0;i<n; ){
            if(s[i]=='1'){
                c++;
                i++;
            }
            if(s[i]=='0'){
                ans += c;
            }
            while(s[i]=='0'){
                i++;
            }
        }
        return ans;
    }
};
