class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
         int prev = 0;
         int ans = 0;
        for(int i=n-2;i>=0;i--){
          if(s[i]=='1'&& s[i+1]=='0'){
            prev++;
            ans = ans+prev;
          }
          else if(s[i]=='1'&& s[i+1]=='1'){
            ans = ans+prev;
          }
        }
        return ans;
    }
};
