class Solution {
public:
    int maxOperations(string s) {
        vector<int>cnt;
        int last=0;
        for(auto t:s){
            if(t=='1') last++;
            else if(last) cnt.push_back(last),last=0;
        }
        if(last) cnt.push_back(last),last=0;
        if(cnt.empty()) return 0;
        if(s.back()=='0') cnt.push_back(0);
        int ans=0; 
        for(int i=0;i<cnt.size()-1;i++){
            ans+=cnt[i];
            cnt[i+1]+=cnt[i];
        }
        return ans;
    }
};
