class Solution {
public:
    int maxOperations(string s) {
        int t=0;
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='1')
                t++;
        }
        int ans=0,c=0, i=s.size()-1;
        while(i>=0&&s[i]=='1')
        {
            i--;
            t--;
        }
        for(;i>=0;i--)
        {
            if(s[i]=='0'&&(i+1==s.size()||s[i+1]=='1'))
            {
                ans+=t;
            }
            if(s[i]=='1')
                t--;
        }
        return ans;
    }
};
