impl Solution {

    pub fn max_operations(s: String) -> i32 {
        let mut lt = 0;
        let mut ans = 0;
        for i in 0..s.len() {
            if s.as_bytes()[i] == b'1' {
                lt += 1;
                continue;
            }
            if i+1 == s.len() || s.as_bytes()[i+1] == b'1' {
                ans += lt;
            }
        }
        ans
    }
}
