class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        vector<int> dp(s.size());
        dp[s.size() - 1] = 0;
        int end = s.size();
        for(int j = s.size() - 1,i = s.size();i >= 0;--i)
        {
            if(s[i] == '1')
            {
                if(end == i + 1)
                {
                    if(i + 1 < s.size())dp[i] = dp[i + 1];
                    end = i;
                }else
                {
                    dp[i] = 1;
                    if(end < s.size())dp[i] += dp[end];
                    end = i;
                }
            }
        }
        return accumulate(dp.begin(),dp.end(),0);
    }
};
