class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int n = s.size();
        int cnt = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                continue;
            }
            if (i>0 && s[i-1] == '0') {
                res += cnt;
            }
            cnt++;
        }

        if (s[n-1] == '0') {
            res += cnt;
        }
        return res;
    }
};
