class Solution {
    public int maxOperations(String s) {
        char[] arr = s.toCharArray();

        int res = 0;
        int cur = 0;
        int zCnt = 0;

        for (char c : arr) {
            if (c == '1') {
                if (zCnt > 0) {
                    res += cur;
                }        
                cur++;
                zCnt = 0;
            } else {
                zCnt++;
            }
        }

        if (zCnt > 0) {
            res += cur;
        }

        return res;
    }
}
