class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int cnt = 0;
        for(int i=0;i<s.size();i++){
            if(s[i] == '1'){
                cnt++;
            }
            if(s[i] == '0' && (i == s.size() - 1 || s[i + 1] == '1'))
                res += cnt;
        }
        return res;
    }
};
