class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int countOnes = 0;
        int result = 0;
        int i = 0;

        while (i < n - 1) {
            if (s[i] == '1') {
                ++countOnes;
            }
            if (s[i] == '1' && s[i + 1] == '0') {
                result += countOnes;
            }
            ++i;
        }

        return result;
    }
};
