class Solution {
public:
    int maxOperations(string s) {
        s.push_back('1');
        int n = s.size();
        int ans = 0, curr = 0, cont = false;
        for (int i = 0; i < n ; ++i) {
            if (s[i] == '1') {
                if (!cont ) {
                    cont = true;
                    ans += curr;
                }
                curr++;
            } else {
                cont = false;
            }
        }
        return ans;
    }
};
