class Solution:
    def maxOperations(self, s: str) -> int:
        i = len(s) - 1
        gz = 0 # group of zeros
        ones = s.count('1')
        res = 0
        while i >= 0:
            if s[i] == '1':
                ones -= 1
                i -= 1
                continue
            else:
                gz += 1
                res += ones
                while i - 1 >= 0 and s[i - 1] == '0':
                    i -= 1
                i -= 1
        return res
