class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        long long sum = 0, cur = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '0')
                continue;

            if (i + 1 < n && s[i + 1] == '0')
                cur++;

            sum += cur;
        }
        return sum;
    }
};
