class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int en = (n - 1);
        while (en >= 0 && s[en] == '1') en--;
        if (en < 0) return 0;
        int cs = 0, ans = 0, cnt = 0;
        for (int i = 0; i <= en; i++) {
            if (s[i] == '1') cnt++;
            else {
                if (cnt > 0) {
                    cs += cnt;
                    ans += cs;
                    cnt = 0;
                }
            }
        }

        return ans;
    }
};
