class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.length();
        int wait = 0;
        int take = 0;
        for (int i=0;i<n;i++) {
            if (s[i] == '1') {
                ans += take;
                wait += take + 1;
                take = 0;
            } else {
                take += wait;
                wait = 0;
            }
        }

        ans += take;

        return ans;
    }
};
