class Solution {
public:
    int maxOperations(string s) {
        int z=0;
        int ans=0;
        stack<int> st;
        vector<int> v;
        for(auto c: s){
            if(c=='0'){
                if(!st.empty() && st.top()==1) st.push(0);
            }
            else st.push(1);
        }
        while(!st.empty()){
            v.push_back(st.top());
            st.pop();
        }

        for(auto it: v){
            if(it==0){
                z++;
            }
            else ans+=z;
        }
        return ans;
    }
};
