class Solution:
    def maxOperations(self, s: str) -> int:
        sections = []
        section = 0
        for c in s:
            if c == "1":
                section += 1
                continue
            if section == 0:
                continue
            sections.append(section)
            section = 0

        if len(sections) == 0:
            return 0
        prefix_sums = [0] * len(sections)
        prefix_sums[0] = sections[0]
        for i in range(1, len(sections)):
            prefix_sums[i] = prefix_sums[i - 1] + sections[i]
        return sum(prefix_sums)
