class Solution:
    def maxOperations(self, s: str) -> int:
        lens = []
        curr = 0
        for c in s:
            if c=='0':
                if curr>0:
                    lens.append(curr)
                curr=0
            else:
                curr += 1
        lens.append(curr)
        lens.reverse()
        i, ans = 1, 0
        #print(lens)
        for i in range(1, len(lens)):
            ans += i*lens[i]
            i += 1
        return ans
