class Solution {
public:
    int maxOperations(string s) {
        vector<int> counts;
        int n = s.size();

        int count = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                if (count) {
                    counts.push_back(count);
                    count = 0;
                }
            } else {
                count++;
            }
        }

        int res = 0;
        int m = counts.size();
        for (int i = 0; i < m; i++)
            res += counts[i] * (m - i);

        return res;
    }
};
