class Solution {
    public int maxOperations(String s) {
        s =s+"1";
        char[] chars = s.toCharArray();
        int aCount = 0;
        int res  = 0;

        for (int i = 0; i < s.length() ; i++) {
            char c = chars[i];
            if (i>0 && c== '1' && chars[i-1] == '0')
                res+=aCount;
            if (c =='1') aCount++;
        }
        return res;
    }
}
