class Solution {
public:
int maxOperations(string s) {
    int ops = 0;
    int onesCount = 0;
    bool prevOne = false;

    for (size_t i = 0; i < s.size(); i++)
    {
        char c = s[i];

        if (c == '0') {
            if (prevOne) {
                ops += onesCount;
            }

            prevOne = false;
        }
        else {
            prevOne = true;
            onesCount++;
        }
    }

    return ops;
}
};
