class Solution {
public:
    int maxOperations(string s) {
        vector<int>pre(s.size(),0);
        for(int i =0 ;i<s.size(); i++){
            if(s[i]=='1')pre[i] = 1;
            if(i)pre[i]+=pre[i-1];
        }
        int ind = s.size()-1;
        int ans = 0;
        if(s.back()=='1')while(ind>=0 && s[ind]!='0')ind--;
        for(int i = ind; i>=0; i--){
            if(s[i]=='1'){
                int j = i; 
                while(j>=0&&s[j]=='1')j--;
                int left = 0;
                if(j>=0)left=pre[j];
                ans+=i-j+left;
                i = j+1;
            }
        }
        return ans;
    }
};
