int maxOperations(char* s) {
    int n = strlen(s);
    int ret = 0, cnt = 0;
    for (int i = 0; i < n; ++i) {
        if (s[i] == '1') {
            cnt++;
        } else {
            ret += cnt;
            while (s[i + 1] == '0')
                ++i;
        }
    }
    return ret;
}
