class Solution {
    public int maxOperations(String s) {
        char [] ch = s.toCharArray();
        int n = ch.length;
        int ans = 0, one = 0;
        for(int i = 0; i<n; i++){
            if(ch[i]=='1'){
                one++;
            }
            else{
                if(i-1>=0 && ch[i-1]=='1'){
                    ans += one;
                }
            }
        }
        return ans;
    }
}
