class Solution {
public:
    int maxOperations(string s) {
        int numof1 = 0;
        int ans = 0;
        bool equlof0 = true;
        for(char ch : s){
            if(ch == '1'){ numof1++; equlof0 = true;}
            else if(equlof0){ ans += numof1 ; equlof0 = false; }
        }
        return ans;

    }
};
