class Solution {
public:
    int maxOperations(string s) {
        int gaps=0,ans=0;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='0')
                continue;
            if(i+1<s.size()&&s[i+1]=='0')
                gaps++;
            ans+=gaps;
        }
        return ans;
    }
};
