class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int idx=n-1;
        while(idx>=0 and s[idx]=='1') idx--;
        int k=0;
        int cur=0;
        int ans=0;
        for(int i=idx; i>=0; i--){
            if(s[i]=='1') cur++;
            if(s[i]=='1' and (i==0 or s[i-1]=='0')){
                k++;
                ans+=cur*k;
                // cout << "ans+=" << cur << "*" << k << '\n';
                cur=0;
            }
        }
        return ans;
    }
};
