class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, ans = 0, n = s.length();
        bool flag = false;
        for (int i = 0; i < n; ++i) {
            if (s[i] == '1') {
                if (flag)
                    ans += cnt;
                ++cnt;
                flag = false;
            }
            if (s[i] == '0')
                flag = true;
        }
        if (flag) ans+=cnt;
        return ans;
    }
};
