class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        s.push_back('1');

        vector<pair<int,int>> up;
        int sum=0;
        for(int i=0;i<n;++i){
            if(s[i]=='1'){
                sum++;
            }
            if(s[i]=='0'&& s[i+1]=='1'){
                up.push_back({i,sum});
            }
        }
        int ans=0;
        sum=0;
        for(auto p:up){
            int s=p.second;
            ans+=s;
        }
        return ans;

    }
};
