class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int prev = 0;
        int i=0;
        while(i<n-1){
            if(s[i]=='1' && s[i+1]=='0'){
                int j=i+1;
                while(j<n && s[j]=='0') j++;
                swap(s[i],s[j-1]);
                ans += ++prev;
                i = j;
                continue;
            }else if(s[i]=='1'){
                prev++;
            }
            i++;
        }

        return ans;
    }
};
