class Solution {
    public int maxOperations(String s) {
        List<Integer>w=new ArrayList<>();
        int cu=0;
        int st=0;
        for(int i=s.length()-1;i>=0;i--){
            if(s.charAt(i)=='1'&&st==1) cu+=1;
            if(s.charAt(i)=='0'){
                if(cu>0) w.add(cu);
                cu=0;
                st=1;
            }
        }
        if(cu!=0) w.add(cu);
        if(w.size()==0) return 0;
        int ans=w.get(0);
        int c=1;
        for(int i=1;i<w.size();i++){
            c+=1;
            ans+=c*w.get(i);
        }
        return ans;
    }
}
