class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(), z=0, cnt=0, ans=0;
        for(int i=0;i<n;i++) {
            if(s[i] == '1') {
                if(z>0) {
                    ans+=cnt;
                    z=0;
                }
                cnt++;
            }
            if(s[i] == '0') z++;
        }
        if(z>0) ans+=cnt;
        return ans;
    }

};
