class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int c = 0;
        int len = s.size();
        int i = 0;
       while(i< len){
            if(s[i]== '1'){
                i++;
                c++;
            }
            else{
                ans += c;
                while(i< len && s[i]== '0')i++;
            }
        }

        return ans;
    }
};
