class Solution {
public:
    int maxOperations(string s) {
        int ans=0,d=0;
        string g;
        for(int i=0;i<s.size();i++){
           if(s[i]=='1'){
               g.push_back('1');
           }
            else{
                if(g.size()>0){
                if(s[i]!=g.back()){
                g.push_back('0');}
                }
                else{
                    g.push_back('0');
                }
            }
        }
        bool flag=0;
        int h=g.size();
        for(int i=h-1;i>=0;i--){
            if(g[i]=='0'){
                d++;
                flag=1;
            }
            else if(flag){
                ans+=d;
            }
        }
        return ans;
    }
};
