class Solution {
    public int maxOperations(String s) {
        int n = s.length();

        int[] nextOne = new int[n];

        int next = n - 1;
        for (int i = n - 2; i >= 0; i--) {
            nextOne[i] = next;
            if (s.charAt(i) == '0' && s.charAt(i + 1) == '1') next = i;
        }

        int result = 0;
        int countOne = 0;
        for (int i = 0; i < n - 1; i++) {
            if (s.charAt(i) == '1') {
                countOne++;
                if (s.charAt(i + 1) == '0') {
                    result += countOne;
                }
            }
        }

        return result;
    }
}
