class Solution {
public:
    int maxOperations(string s) {
        s = "0" + s;
        int cnt = 0;
        long long ans = 0;
        for (int i = 1; i < (int)s.size(); ++i) {
            if (s[i] == '0' && s[i-1] == '1') {
                ans += cnt;
            }
            else if (s[i] == '1') cnt++;
        }
        return ans;
    }
};
