class Solution {
public:
    int maxOperations(string s) {
        int cur=0;
        int ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                cur++;
            }
            else{
            while((i<s.size())&&(s[i]=='0')){
                i++;
            }
            ans+=cur;
            // cout<<ans<<endl;
            i--;
            }
        }
        return ans;
    }
};
