class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, curr = 0, sw = 0;
        for (auto ch: s) {
            if (ch == '1') ++curr, sw = 0;
            else if (sw == 0) {
                sw = 1;
                ans += curr;
            }
        }
        return ans;
    }
};
