class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        char prev = '1';

        int n = s.length();

        vector<int> v(n,0);

        for(int i=n-1;i>=0;i--){
            if(s[i]=='0' && prev=='1')
                cnt++;

            if(s[i]=='1')
                v[i] = cnt;

            prev = s[i];

        }

        int ans = 0;

        for(int i=0;i<n;i++){
            ans +=v[i];
        }
        return ans;
    }
};
