class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),ans=0,cnt=0;
        for(int i=0;i<n;){
            if(s[i]=='1'){
                cnt++;
                i++;
                continue;
            }
            while(i<n && s[i]=='0'){
                i++;
            }

            ans+=cnt;
        }

        return ans;
    }
};
