class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        vector<pair<int,int>> arr;
        for(int i=0;i<n;){
            if(s[i]=='0') i++;
            else{
                int l=i,r=i;
                while(i<n && s[i]=='1'){
                    r=i;
                    i++;
                }
                arr.push_back({l,r});
            }
        }
        // for(auto &it: arr) cout<<it.first<<" "<<it.second<<endl;
        if(arr.size()){
        int cnt=arr[0].second-arr[0].first+1;
        for(int i=1;i<arr.size();i++){
            if((arr[i].first-arr[i-1].second)>1){
                ans+=cnt;
                cnt+=(arr[i].second-arr[i].first+1);
            }
        }
        int last=arr[arr.size()-1].second;
        if(last<(n-1)) ans+=cnt;
        }
        return ans;
    }
};
