class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int ones=0;
        int i=0;
        while(i<s.size()){
            if(s[i]=='1'){
                ans+=ones;
                while(s[i]=='1'){
                ones++;
                i++;
              }
            }
            else{
                i++;
            }
        }
        if(s[s.size()-1]=='0')ans+=ones;
        return ans;
    }
};
