class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        z = 0
        n = len(s)
        for i in reversed(range(n)):
            if s[i] == '1':
                res += z
            else:
                if i == n - 1 or s[i+1] == '1':
                    z += 1
        return res
