class Solution {
public:
    int maxOperations(string s) {
  int n=s.size();
  int ans=0,num=0;
        for(int i=n-1;i>=0;i--){
          if(s[i]=='1'){
            if(i+1<n&&s[i+1]=='0'){
              num++;
            }
            ans+=num;
          }
        }
        return ans;
}
};
