class Solution {
public:
    int pre[100010];
    int maxOperations(string s) {
    int ans=0;
    for (int i = 1; i <=s.size(); ++i) {
        if (s[i-1]=='1')pre[i]=pre[i-1]+1;
        else pre[i]=pre[i-1];
    }
    for (int i = 2; i <=s.size(); ++i) {
        if (s[i-1]=='0'&&s[i-2]=='1')ans+=pre[i];
    }
    return ans;
    }
};
