class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        char prev = '$';
        for(auto &c : s)
        {
            if(c == '1' && prev == '0')
            {
                ans += cnt;

            }
            if(c == '1')cnt++;
            prev = c;
        }
        if(s.back() == '0')ans += cnt;
        return ans;
    }
};
