class Solution {
    public int maxOperations(String s) {
        char[] chars = s.toCharArray();
        int n = chars.length;
        int cnt = 0;
        int ans = 0;
        for (int i = 0; i < n - 1; i++) {
            if (chars[i] == '1') {
                cnt++;
            }
            if (chars[i] == '1' && chars[i + 1] == '0') {
                ans += cnt;
            }
        }
        return ans;
    }
}
